require("Font8x16").add(Graphics);

const font = 4;
const white = '#fff';
const wnd = require("heatshrink").decompress(atob(""))
const logo = require("heatshrink").decompress(atob(""))

function window() {
  g.drawImage(logo, 74, 24, {scale: 2.0});
  g.drawImage(wnd, 24, 104);
  g.drawImage(wnd, 72, 104);
  g.drawImage(wnd, 130, 104);
  g.drawImage(wnd, 176, 104);
}

function drawClock() {
  var t = new Date();
  var h = t.getHours();
  var m = t.getMinutes();
  var time = (("0" + h).substr(-2) + ("0" + m).substr(-2)).split('');

  g.setFont("8x16", font);
  g.setFontAlign(0, 0, 0);
  g.drawString(time[0], 48, 140);
  g.drawString(time[1], 96, 140);
  g.drawString(time[2], 154, 140);
  g.drawString(time[3], 200, 140);

}

function drawAll() {
  g.clearRect(24, 100, 230, 160);
  window();  
  g.setColor(white);
  drawClock();
}

Bangle.on('lcdPower', function(on) {
  if (on) drawAll();
});

g.clear();
// Show launcher when button pressed
Bangle.setUI("clock");
Bangle.loadWidgets();
Bangle.drawWidgets();
setInterval(drawAll, 1E4);
drawAll();
